import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:parallax_image/parallax_image.dart';void main() => runApp(MyApp());class MyApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    return MaterialApp(      debugShowCheckedModeBanner: false,      home: Demo(),    );  }}class Demo extends StatelessWidget {  Widget _verticalChild(BuildContext context,int index){    index++;    if(index > 6)return null;    return Padding(      padding: EdgeInsets.all(10),      child: ParallaxImage(          image: ExactAssetImage('assets/img$index.jpg'),          extent: 200      ),    );  }  Widget _horizontalChild(BuildContext context,int index){    index++;    if(index > 5)return null;    return Padding(      padding: EdgeInsets.all(10),      child: ParallaxImage(          image: ExactAssetImage('assets/img$index.jpg'),          extent: 100      ),    );  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text('Parallax Demo'),      ),      body: Column(        children: [          Padding(padding: EdgeInsets.all(20),          child: Text('Horizontal',style: TextStyle(fontWeight: FontWeight.bold,fontSize: 20),),),          Container(            constraints: BoxConstraints(maxHeight: 200),            child: ListView.builder(                itemBuilder: _horizontalChild,              scrollDirection: Axis.horizontal,            ),          ),          Padding(padding: EdgeInsets.all(20),          child: Text('Vertical',style: TextStyle(fontSize: 20,fontWeight: FontWeight.bold),),          ),          Expanded(              child: ListView.builder(                  itemBuilder: _verticalChild              ),          ),        ],      ),    );  }}